var fs = require('fs');

var BosClient = require('./src/bos_client');
var helper = require('./src/helper');

var client = new BosClient({
    endpoint: 'http://bj.bcebos.com',
    credentials: {
        ak: 'ydFi9KR2YOrvHlmGD3oYKEWW',
        sk: 'KGCc1x4KEpSVmXUu1gOfutqMDmxf0Hvn'
    }
});
client.on('progress', function (state) {
    console.log(state);
});

var bucket = 'bce-bos-uploader';
var object = '2016/06/23/1G.rar';
var data = '/Volumes/HDD/Users/leeight/1G.rar';
var MB = 1024 * 1024;

